Plugin URI: 
Description: Adds is_cache_group() and get_cache_group() functions.
Version: 0.0.2
Author: MonkeyMediaInc
Author URI: http://www.monkeymediainc.com 
License: 
*/
   
class monkey_cache_groups {
	
	public $_class;
	private $options;

	function __construct() {
		$this->_class = strtolower(get_class($this));
		
		if ( is_admin() ) {
			add_action('admin_menu', array( &$this , 'register_monkey_menu_pages' ));
			add_action('admin_init', array(&$this, 'plugin_admin_init' ));
		}
		add_action('template_redirect', array(&$this, 'mcg_frontend_init'));
		add_action('plugins_loaded', array (&$this, 'mcg_cache_group_functions') );
	}
	
	function register_monkey_menu_pages() {
		if ( empty ( $GLOBALS['admin_page_hooks']['monkey_dashboard'] ) ) {
			$icon_svg = 'data:image/svg+xml;base64,';
			add_menu_page( 'Monkey Plugins', 'Monkey Plugins', 'manage_options', 'monkey_dashboard', '__return_false', $icon_svg );
		}
		add_submenu_page( 'monkey_dashboard', 'WPE Cache Groups', 'WPE Cache Groups', 'manage_options', 'monkey_cache_groups', array(&$this, 'options_page') );
	}
	
	function plugin_admin_init(){
		register_setting( $this->_class.'_options', $this->_class.'_options', array(&$this, 'sanitize') );
		add_settings_section('plugin_main', 'Main Settings', '__return_false', $this->_class.'_settings');
		add_settings_field('wpe_js_var', 'Generate Javascrip Variable for wpe_cache_group', array(&$this, 'wpe_js_var_settings_callback'), $this->_class.'_settings', 'plugin_main');
	}
	
	public function sanitize( $input ) {
        $new_input = array();
		if (!is_array($input)) $input = array($input);
        
		foreach($input as $key => $value) {
			$new_input[$key] = intval($value);
		}
		
		add_settings_error( $this->_class, esc_attr( 'settings_updated' ), 'Successfully updated', 'updated' );

        return $new_input;
    }
	
	function options_page() {
		$this->options = get_option( $this->_class.'_options' );
		?>
		<h2>WPE Cache Groups</h2>
		<?php settings_errors(); ?>
		<form action="options.php" method="post">
			<?php settings_fields($this->_class.'_options'); ?>
			<?php do_settings_sections($this->_class.'_settings'); ?>
			<input name="Submit" type="submit" value="<?php esc_attr_e('Save Changes'); ?>" />
		</form>
		<?php
	}
	
	function wpe_js_var_settings_callback() {
		printf('<input type="checkbox" name="%s" value="1" %s />', $this->_class.'_options[wpe_js_var]', !empty( $this->options['wpe_js_var'] ) ? 'checked' : '');
	}
	
	function mcg_frontend_init() {
		$this->options = get_option( $this->_class.'_options' );
		
		if($this->options['wpe_js_var']) {
			add_action('wp_head', array($this, 'set_js_cache_group'), 1);
		}
	}
	
	function mcg_cache_group_functions() {
		if(!function_exists('is_cache_group')) {
			function is_cache_group($group = '') {
				$headers = apache_request_headers();
				if(array_key_exists('X-Cache-Group', $headers) && (empty($group) || $headers['X-Cache-Group'] == $group))
					return true;
				else
					return false;
			}
		}
		
		if(!function_exists('get_cache_group')) {
			function get_cache_group() {
				$headers = apache_request_headers();
				if(array_key_exists('X-Cache-Group', $headers))
					return $headers['X-Cache-Group'];
				else
					return;
			}
		}
	}
	
	function set_js_cache_group() {
		if(function_exists('get_cache_group')) { ?>
			<script>var window.wpe_cache_group = "<?php echo get_cache_group() ?>";</script>
		<?php }
	}
}
new monkey_cache_groups;